import React, { useEffect, useState } from "react";
import "./headertravel.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHotel,
  faPlane,
  faTrain,
  faBed,
  faCalendarDays,
  faPerson,
  faLocation,
} from "@fortawesome/free-solid-svg-icons";
import "@fortawesome/fontawesome-svg-core/styles.css";
import { Link, useLocation } from "react-router-dom";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file
import { format } from "date-fns";
import axios from "axios";

const Headertravel = ({ onSearch }) => {
  const location = useLocation();
  const [openOptions, setOpenOptions] = useState(false);
  const [options, setOptions] = useState({
    adult: 1,
    children: 0,
  });
  const [arrive, setArrive] = useState("");
  const [dest, setDest] = useState("");
  const [dates, setDates] = useState(new Date());
  const [selectedItinerary, setSelectedItinerary] = useState("ROUND_TRIP");
  const [selectedSortOrder, setSelectedSortOrder] = useState("price");
  const [selectedClassOfService, setSelectedClassOfService] =
    useState("ECONOMY");
  const [flightResults, setFlightResults] = useState(null);
  const searchFlights = async (
    sourceAirportCode,
    destinationAirportCode,
    date,
    itineraryType,
    sortOrder,
    classOfService,
    adult
  ) => {
    const options = {
      method: "GET",
      //   url: "https://tripadvisor16.p.rapidapi.com/api/v1/flights/searchFlights",
      params: {
        sourceAirportCode: sourceAirportCode,
        destinationAirportCode: destinationAirportCode,
        date: date,
        itineraryType: itineraryType,
        sortOrder: sortOrder,
        numAdults: adult,
        numSeniors: "0",
        classOfService: classOfService,
        pageNumber: "1",
        currencyCode: "INR",
      },
      headers: {
        "X-RapidAPI-Key": "d2ee623077msh2c821438ec02590p15b628jsne1fb3565a190",
        "X-RapidAPI-Host": "tripadvisor16.p.rapidapi.com",
      },
    };

    try {
      const response = await axios.request(options);
      console.log(response.data);
      setFlightResults(response.data);
    } catch (error) {
      console.error(error);
      // Handle errors here
    }
  };

  useEffect(() => {
    searchFlights(
      arrive,
      dest,
      dates,
      selectedItinerary,
      selectedSortOrder,
      selectedClassOfService
    );
  }, []);

  const handleOption = (name, operation) => {
    setOptions((prev) => {
      return {
        ...prev,
        [name]: operation === "i" ? options[name] + 1 : options[name] - 1,
      };
    });
  };
  const handleSearch = () => {
    const searchData = {
      arrive,
      dest,
      dates,
      options,
      selectedItinerary,
      selectedSortOrder,
      selectedClassOfService,
    };

    onSearch(searchData);
  };
  return (
    <div>
      <div className="header">
        <div className="beach-image">
          <img
            src="https://c.animaapp.com/BWfkNfae/img/rectangle-86.png"
            alt=""
          />
        </div>
        <div className="content">
          <h2>A lifetime of discounts?It's Genius.</h2>
          <h3>
            Get rewarded for your travels-unlock instant savings of 10% or more
            with a free Travello account
          </h3>
        </div>
      </div>

      <div className="searchContainer">
        <div className="headerList">
          <div className="headerListItem active">
            <FontAwesomeIcon icon={faHotel} />
            <Link to="/hotel">
              <span>Hotels</span>
            </Link>
          </div>
          <div className="headerListItem ">
            <FontAwesomeIcon icon={faPlane} />
            <Link
              to="/travel"
              style={{ color: "inherit", textDecoration: "none" }}
            >
              Flights
            </Link>
          </div>
          <div className="headerListItem ">
            <FontAwesomeIcon icon={faTrain} />
            <span>Trains</span>
          </div>
          <div className="headerListItem ">
            <FontAwesomeIcon icon={faHotel} />
            <span>Packages</span>
          </div>
        </div>

        {
          <div className="headerSearch">
            <div className="headerSearchItem">
              <FontAwesomeIcon icon={faLocation} className="headerIcon" />
              <input
                type="text"
                placeholder="From"
                className="headerSearchInput"
                onChange={(e) => setArrive(e.target.value)}
              />
            </div>
            <div className="headerSearchItem">
              <FontAwesomeIcon icon={faLocation} className="headerIcon" />
              <input
                type="text"
                placeholder="To"
                className="headerSearchInput"
                onChange={(e) => setDest(e.target.value)}
              />
            </div>

            <div className="headerSearchItem">
              <FontAwesomeIcon icon={faCalendarDays} className="headerIcon" />
              <input
                type="date"
                placeholder="Depart Date"
                className="headerSearchInput"
                onChange={(e) => setDates(e.target.value)}
              />
            </div>
            <div className="headerSearchItem">
              <FontAwesomeIcon icon={faPerson} className="headerIcon" />
              <span
                onClick={() => setOpenOptions(!openOptions)}
                className="headerSearchText"
              >
                {`${options.adult} adult · ${options.children} children `}
              </span>

              {openOptions && (
                <div className="options">
                  <div className="optionItem">
                    <span className="optionText">Adult</span>
                    <div className="optionCounter">
                      <button
                        disabled={options.adult <= 1}
                        className="optionCounterButton"
                        onClick={() => handleOption("adult", "d")}
                      >
                        -
                      </button>
                      <span className="optionCounterNumber">
                        {options.adult}
                      </span>
                      <button
                        className="optionCounterButton"
                        onClick={() => handleOption("adult", "i")}
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <div className="optionItem">
                    <span className="optionText">Children</span>
                    <div className="optionCounter">
                      <button
                        disabled={options.children <= 0}
                        className="optionCounterButton"
                        onClick={() => handleOption("children", "d")}
                      >
                        -
                      </button>
                      <span className="optionCounterNumber">
                        {options.children}
                      </span>
                      <button
                        className="optionCounterButton"
                        onClick={() => handleOption("children", "i")}
                      >
                        +
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
            <div className="headerSearchItem">
              <label htmlFor="selectedItinerary" className="okok">
                Select Itinerary
              </label>
              <select
                value={selectedItinerary}
                onChange={(e) => setSelectedItinerary(e.target.value)}
              >
                <option value="ROUND_TRIP">Round Trip</option>
                <option value="ONE_WAY">One Way</option>
              </select>
            </div>
            <div className="headerSearchItem">
              <label htmlFor="selectedSortOrder" className="okok">
                Select Sort Order
              </label>
              <select
                value={selectedSortOrder}
                onChange={(e) => setSelectedSortOrder(e.target.value)}
              >
                <option value="price">Price</option>
                <option value="duration">Duration</option>
              </select>
            </div>
            <div className="headerSearchItem">
              <label htmlFor="classOfService" className="okok">
                Class Of Service
              </label>
              <select
                value={selectedClassOfService}
                onChange={(e) => setSelectedClassOfService(e.target.value)}
              >
                <option value="ECONOMY">Economy</option>
                <option value="BUSSINESS">Business</option>
                {/* Add other class of service options */}
              </select>
            </div>
          </div>
        }
        <div className="Search-btn">
          <button className="search" onClick={handleSearch}>
            Search
          </button>
        </div>
      </div>
      {flightResults && (
        <div className="flight-results">
          <h2>Flight Search Results</h2>
          {/* Render flight data here */}
          {flightResults && (
            <div>
              {flightResults.data.flights.map((flight, index) => (
                <div key={index}>
                  <p>
                    Flight Number: {flight.segments[0].legs[0].flightNumber}
                  </p>
                  <p>
                    Departure Time:{" "}
                    {flight.segments[0].legs[0].departureDateTime}
                  </p>
                  <p>
                    Arrival Time: {flight.segments[0].legs[0].arrivalDateTime}
                  </p>
                  {/* Add more flight details as needed */}
                  <hr /> {/* Add a separator */}
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Headertravel;
