import { React, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

import "./about.css";

const About = () => {
  const { user, dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch({ type: "LOGOUT" });
    navigate("/");
  };
  return (
    <div>
      <div className="about">
        {user ? (
          <div className="navItems">
            <span className="logo">Travello</span>
            <span className="username">{user.username}</span>
            <button className="logout" onClick={handleLogout}>
              Logout
            </button>
          </div>
        ) : (
          <div className="navItems">
            <span className="logo">Travello</span>
            <Link
              to="/register"
              className="register-1"
              style={{
                textDecoration: "none",
              }}
            >
              Create Account
            </Link>
            <Link
              to="/register"
              className="login-1"
              style={{ textDecoration: "none" }}
            >
              Login
            </Link>
          </div>
        )}

        <div className="overflow">
          <p className="Welcome">Welcome to the Travello</p>
          <p className="subheading">
            where we turn your travel dreams into unforgettable adventures.
          </p>
        </div>
      </div>

      <div class="container">
        <div class="horizontal-bar"></div>
      </div>

      <div className="heading1">
        <p>PUTTING TRAVEL IN YOUR HAND</p>
        <P>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Obcaecati a
          aliquam pariatur similique quas nobis doloribus dolore minima, ea, cum
          magni hic dicta. Minus in commodi nostrum facere quia sunt id
          voluptatem mollitia necessitatibus fuga enim eum illum repellat
          aperiam quibusdam nesciunt, porro alias, suscipit praesentium
          inventore? Expedita, animi vero.
        </P>
      </div>
    </div>
  );
};

export default About;
